<template>
  <div
    class="lore-box"
    :style="
      'top: ' +
      top +
      'px; left: ' +
      left +
      'px; display: ' +
      (showLore ? 'inline-block' : 'none')
    "
  >
    <div class="lore-body" v-html="lore"></div>
  </div>
</template>

<script>
export default {
  name: "Lore",
  data() {
    return {
      top: 0,
      left: 0,
      showLore: false,
      lore: "lore",
      direction: false,
    };
  },
  methods: {
    checkOverflow() {
      return (
        this.$el.offsetWidth +
          this.left +
          (this.direction ? this.$el.offsetWidth + 40 : 0) >=
        window.innerWidth
      );
    },
    updateLore(event) {
      this.left =
        event.clientX + (this.direction ? -20 - this.$el.offsetWidth : 20);
      if (window.innerHeight < event.clientY + this.$el.offsetHeight) {
        this.top =
          event.clientY -
          20 +
          (window.innerHeight - event.clientY - this.$el.offsetHeight);
      } else {
        this.top = event.clientY - 20;
      }
      this.direction = this.checkOverflow();
    },
    parseLore() {
      let recombAdded = false;
      const lines = this.lore.split("\n");
      this.lore = "";
      for (let lineIndex in lines) {
        let tokens = lines[lineIndex].split("ยง");
        for (let tokenIndex in tokens) {
          let token = tokens[tokenIndex];
          let colorCode = token.charAt(0);
          let boldCase = token.charAt(0);
          if (colorCode.trim() === "") {
            continue;
          }
          token = token.substring(1);
          switch ((colorCode, boldCase)) {
            case "4":
              colorCode = "#AA0000";
              break;
            case "c":
              colorCode = "#FF5555";
              break;
            case "6":
              colorCode = "#FFAA00";
              break;
            case "e":
              colorCode = "#FFFF55";
              break;
            case "2":
              colorCode = "#00AA00";
              break;
            case "a":
              colorCode = "#55FF55";
              break;
            case "b":
              colorCode = "#55FFFF";
              break;
            case "3":
              colorCode = "#00AAAA";
              break;
            case "1":
              colorCode = "#0000AA";
              break;
            case "9":
              colorCode = "#5555FF";
              break;
            case "d":
              colorCode = "#FF55FF";
              break;
            case "5":
              colorCode = "#AA00AA";
              break;
            case "f":
              colorCode = "#FFFFFF";
              break;
            case "7":
              colorCode = "#AAAAAA";
              break;
            case "8":
              colorCode = "#555555";
              break;
            case "0":
              colorCode = "#000000";
              break;
            default:
              colorCode = "#FFFFFF";
              break;
            case "l":
              boldCase = "lore-body-bold";
              break;
          }
          if (lineIndex == lines.length - 1) {
            if (token.includes("LEGENDARY")) {
              colorCode = "#FFAA00";
              boldCase = "lore-body-bold";
            } else if (token.includes("EPIC")) {
              colorCode = "#AA00AA";
              boldCase = "lore-body-bold";
            } else if (token.includes("MYTHIC")) {
              colorCode = "#FF55FF";
              boldCase = "lore-body-bold";
            } else if (token.includes("UNCOMMON")) {
              colorCode = "#55FF55";
              boldCase = "lore-body-bold";
            } else if (token.includes("RARE")) {
              colorCode = "#5555FF";
              boldCase = "lore-body-bold";
            } else if (token.includes("DIVINE")) {
              colorCode = "#55FFFF";
              boldCase = "lore-body-bold";
            } else if (token.includes("SPECIAL")) {
              colorCode = "#FF5555";
              boldCase = "lore-body-bold";
            }

            if (token == "a") {
              token = "";
              if (!recombAdded) {
                recombAdded = true;
                this.lore +=
                  '<img style="width: 20px; display: inline-block;" src="data:image/png;base64,"/>';
              }
            }
          }

          this.lore +=
            "<span class='" +
            boldCase +
            "' style='" +
            "color: " +
            colorCode +
            "'>" +
            token +
            "</span>";
        }
        this.lore += "\n";
      }
    },
  },
  mounted() {
    this.$root.Lore = this;
  },
};
</script>

<style>
@font-face {
  font-family: MC;
  src: url(data:font/truetype;base64,);
}

@font-face {
  font-family: MCBOLD;
  src: url(data:font/truetype;base64,);
}

.lore-box {
  background-color: rgba(16, 1, 16, 0.9);
  position: fixed;
  z-index: 10000;
  border-radius: 0.2rem;
  pointer-events: none;
  min-width: 250px;
}

.lore-body {
  border: 2px solid rgb(37, 1, 90);
  margin: 2px;
  border-radius: 0.2rem;
  width: calc(100% - 4px);
  height: calc(100% - 4px);
  padding: 5px;
  white-space: pre-wrap;
  font-size: 0.8em;
  font-family: MC;
}
.lore-body-bold {
  font-size: 1.1em;
  font-family: MCBOLD;
}
</style>
